import { render } from "@testing-library/react";
import React from "react";
import ReactDOM from "react-dom/client"; //from react-dom/client, not react
import './index.css';

class Square extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      value: null,
    };
  }
  render() {
    return (
      <button className="square" onClick={() =>
      this.setState({value: 'X'})}>
        {this.state.value}
      </button>
    );
  }
}

class Board extends React.Component {
  renderSquare(i) {
    return <Square value={i} />;
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

//test stuff
class Beep extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      freq: 440,
      playing: false,
      context: null,
    };
  }
  init(){
    this.setState({context: window.AudioContext});
  }
  play() {
    var oscillator = this.state.context.createOscillator();

    oscillator.type = 'square';
    oscillator.frequency.setValueAtTime(this.state.freq, this.context.currentTime);
    oscillator.connect(this.context.destination);
    oscillator.start();
  }
  render(){
    return (
      <button className="key" onClick={ () => {
        this.init()
        //this.play()
       } }>Play</button>
    );
  }
}
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Beep/>);
